choose.when:
				select *
				from cocktail_recipe
				where cocktailLevel = #{level}
				order by cocktailNo asc 
        		limit #{skip},#{amount}

choose.otherwise:
select * from cocktail_recipe
where cocktailName like concat('%',#{keyword}, '%')
order by cocktailNo asc limit #{skip},#{amount}

choose.when:
select count(*)
from cocktail_recipe
where cocktailLevel = #{level}
